import React from 'react';
import PropTypes from 'prop-types';
import { useQuery } from '@apollo/react-hooks';
import { useField } from 'formik';
import { motion, AnimatePresence } from 'framer-motion';
import uniqBy from 'lodash/uniqBy';
import isEmpty from 'lodash/isEmpty';
import { getMeatsQuery } from 'gql';

const SearchResult = props => {
	const [field, meta] = useField('searchString');
	const { value: meatName } = meta;
	const { data, loading } = useQuery(getMeatsQuery);

	if (meatName.length < 2 || loading) {
		return null;
	}

	const filterName = data.allMeats.filter(meat => meat.meatName.toLowerCase().includes(meatName.toLowerCase()));
	const filterTags = data.allMeats.filter(meat => meat.meatGroup.toLowerCase().includes(meatName.toLowerCase()));
	const result = uniqBy([...filterName, ...filterTags], 'meatName');

	if (isEmpty(result)) {
		return null;
	}

	return (
		<motion.div transition={{ staggerChildren: 0.1, delayChildren: 0.5 }}>
			{result.map(meat => {
				console.log('meat', meat);
				return (
					<motion.div
						className="my-4 bg-white rounded overflow-hidden shadow-lg"
						key={meat.meatName}
						initial={{ y: 50, opacity: 0 }}
						animate={{ y: 0, opacity: 1 }}
					>
						<div className="mx-6 my-4">
							<div className="font-bold text-xl text-grey-900 mb-2"> {meat.meatName}</div>
							<p className="text-grey-700 text-base">{meat.shortDescription}</p>
						</div>
						<div className="mx-6 my-4">
							<span className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900 mr-2">
								#photography
							</span>
							<span className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900 mr-2">
								#travel
							</span>
							<span className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900">
								#winter
							</span>
						</div>
					</motion.div>
				);
			})}
		</motion.div>
	);
};

SearchResult.propTypes = {};

export default SearchResult;
