import React, { useState } from 'react';
import { motion } from 'framer-motion';

const variants = {
	initial: { y: 50, opacity: 0 },
	reveal: { y: 0, opacity: 1 },
};

const SearchResultItem = props => {
	const { meatName, shortDescription } = props;
	const [open, setOpen] = useState(false);

	let classes = 'my-4 bg-white rounded overflow-hidden shadow-lg top-0 right-0 bottom-0 left-0';

	if (open) {
		classes += 'fixed';
	}

	return (
		<motion.div onClick={() => setOpen(!open)} layoutTransition variants={variants} className={classes}>
			<div className="mx-6 my-4">
				<div className="font-bold text-xl text-grey-900 mb-2"> {meatName}</div>
				<p className="text-grey-700 text-base">{shortDescription}</p>
			</div>
			<div className="mx-6 my-4">
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#photography
				</motion.span>
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#travel
				</motion.span>
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#winter
				</motion.span>
			</div>
		</motion.div>
	);
};

export default SearchResultItem;
