import React from "react";
import PropTypes from "prop-types";
import { useQuery } from "react-apollo";
import { useField } from "formik";
import { motion } from "framer-motion";
import uniqBy from "lodash/uniqBy";
import isEmpty from "lodash/isEmpty";
import { getMeatsQuery } from "gql";

const SearchResult = props => {
  const [field, meta] = useField("searchString");
  const { value: meatName } = meta;
  const { data, loading } = useQuery(getMeatsQuery);

  if (loading) {
    return null;
  }

  const filterName = data.allMeats.filter(meat =>
    meat.meatName.toLowerCase().includes(meatName.toLowerCase())
  );

  const filterTags = data.allMeats.filter(meat =>
    meat.meatGroup.toLowerCase().includes(meatName.toLowerCase())
  );

  const result = uniqBy([...filterName, ...filterTags], "meatName");

  if (isEmpty(result)) {
    return null;
  }

  return (
    <motion.div transition={{ staggerChildren: 0.07, delayChildren: 0.2 }}>
      {result.map(meat => {
        return (
          <motion.div
            key={meat.meatName}
            initial={{ y: -30 }}
            animate={{ y: 0 }}
          >
            {meat.meatName}
          </motion.div>
        );
      })}
    </motion.div>
  );
};

SearchResult.propTypes = {};

export default SearchResult;
