import React from "react";
import PropTypes from "prop-types";
import { useQuery } from "react-apollo";
import { useField } from "formik";
import { motion, AnimatePresence } from "framer-motion";
import uniqBy from "lodash/uniqBy";
import isEmpty from "lodash/isEmpty";
import { getMeatsQuery } from "gql";

const SearchResult = props => {
  const [field, meta] = useField("searchString");
  const { value: meatName } = meta;
  const { data, loading } = useQuery(getMeatsQuery);

  if (meatName.length < 2 || loading) {
    return null;
  }

  const filterName = data.allMeats.filter(meat =>
    meat.meatName.toLowerCase().includes(meatName.toLowerCase())
  );

  const filterTags = data.allMeats.filter(meat =>
    meat.meatGroup.toLowerCase().includes(meatName.toLowerCase())
  );

  const result = uniqBy([...filterName, ...filterTags], "meatName");

  if (isEmpty(result)) {
    return null;
  }

  return (
    <motion.div transition={{ staggerChildren: 0.1, delayChildren: 0.5 }}>
      {result.map(meat => {
        return (
          <motion.div
            class="max-w-sm rounded overflow-hidden shadow-lg"
            key={meat.meatName}
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
          >
            <div class="px-6 py-4">
              <div class="font-bold text-xl mb-2"> {meat.meatName}</div>
              <p class="text-gray-700 text-base">
                Lorem ipsum dolor sit amet, consectetur adipisicing elit.
                Voluptatibus quia, nulla! Maiores et perferendis eaque,
                exercitationem praesentium nihil.
              </p>
            </div>
            <div class="px-6 py-4">
              <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">
                #photography
              </span>
              <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2">
                #travel
              </span>
              <span class="inline-block bg-gray-200 rounded-full px-3 py-1 text-sm font-semibold text-gray-700">
                #winter
              </span>
            </div>
          </motion.div>
        );
      })}
    </motion.div>
  );
};

SearchResult.propTypes = {};

export default SearchResult;
