import React from 'react';
import { motion } from 'framer-motion';

const variants = {
	initial: { y: 50, opacity: 0 },
	reveal: { y: 0, opacity: 1 },
};

const SearchResultItem = props => {
	const { meatName, shortDescription } = props;

	return (
		<motion.div
			whileHover={{ scale: 1.03 }}
			variants={variants}
			className="my-4 bg-white rounded overflow-hidden shadow-lg"
		>
			<div className="mx-6 my-4">
				<div className="font-bold text-xl text-grey-900 mb-2"> {meatName}</div>
				<p className="text-grey-700 text-base">{shortDescription}</p>
			</div>
			<div className="mx-6 my-4">
				<span className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900">
					#photography
				</span>
				<span className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900">
					#travel
				</span>
				<span className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900">
					#winter
				</span>
			</div>
		</motion.div>
	);
};

export default SearchResultItem;
