import React from 'react';

const Pill = ({ active, onClick: handleClick, children }) => {
	let classNames = 'inline-block border border-blue-500 rounded py-1 px-3 bg-blue-500 text-white';

	if (active) {
		classNames =
			'inline-block border border-white rounded hover:border-gray-200 text-blue-500 hover:bg-gray-200 py-1 px-3';
	}

	return (
		<a onClick={handleClick} className={classNames} href="#">
			{children}
		</a>
	);
};

export default Pill;
