import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Pill from 'components/Pill';

const variants = {
	initial: { y: 50, opacity: 0 },
	reveal: { y: 0, opacity: 1 },
};

const openProps = {
	initial: { height: 0 },
	animate: { height: 'auto' },
	exit: { height: 0 },
};

const SearchResultItem = props => {
	const { meatName, shortDescription, stew, oven, fry } = props;
	const [open, setOpen] = useState(false);
	const [tabIndex, setTabIndex] = useState(0);

	function handleTabClick(index) {
		return event => {
			event.preventDefault();
			setTabIndex(index);
		};
	}

	if (open) {
		console.log(props);
	}

	const classes = 'cursor-pointer bg-white rounded-lg overflow-hidden shadow-lg my-4';

	return (
		<motion.div positionTransition onTap={() => setOpen(!open)} variants={variants} className={classes}>
			<AnimatePresence>
				{open && (
					<motion.div className="mx-5 my-4 overflow-hidden" {...openProps}>
						<img src="https://placeimg.com/640/480/any" className="max-w-full rounded-lg shadow-lg" />
					</motion.div>
				)}
			</AnimatePresence>
			<div className="mx-5 my-4">
				<div className="font-bold text-xl text-grey-900 mb-2"> {meatName}</div>
				<p className="text-grey-700 text-base">{shortDescription}</p>
			</div>

			<AnimatePresence>
				{open && (
					<motion.div className="mx-5 my-6 overflow-hidden" {...openProps}>
						<div className="inline-block">
							<Pill active={tabIndex === 0} onClick={handleTabClick(0)}>
								Stekpanna
							</Pill>
						</div>
						<div className="inline-block ml-2">
							<Pill active={tabIndex === 1} onClick={handleTabClick(1)}>
								Ugn
							</Pill>
						</div>
						<div className="inline-block ml-2">
							<Pill active={tabIndex === 2} onClick={handleTabClick(2)}>
								Grill
							</Pill>
						</div>

						{tabIndex === 0 && <p className="my-3">{fry}</p>}

						{tabIndex === 1 && <p className="my-3">{stew}</p>}

						{tabIndex === 2 && <p className="my-3">{grill}</p>}
					</motion.div>
				)}
			</AnimatePresence>

			<div className="mx-5 my-4">
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#photography
				</motion.span>
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#travel
				</motion.span>
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#winter
				</motion.span>
			</div>
		</motion.div>
	);
};

export default SearchResultItem;
