import React, { useState } from 'react';
import { motion } from 'framer-motion';

const variants = {
	initial: { y: 50, opacity: 0 },
	reveal: { y: 0, opacity: 1 },
};

const openSpring = { type: 'spring', stiffness: 200, damping: 30 };
const closeSpring = { type: 'spring', stiffness: 300, damping: 35 };

const SearchResultItem = props => {
	const { meatName, shortDescription } = props;
	const [open, setOpen] = useState(false);

	let classes = 'cursor-pointer bg-white rounded overflow-hidden shadow-lg max-w-lg';

	if (open) {
		classes += ' absolute';
	} else {
		classes += ' my-4';
	}

	return (
		<motion.div
			onTap={() => setOpen(!open)}
			layoutTransition={open ? openSpring : closeSpring}
			variants={variants}
			className={classes}
		>
			{open && (
				<div className="mx-6 my-4">
					<img src="https://placeimg.com/640/480/any" className="max-w-full rounded-lg shadow-lg" />
				</div>
			)}
			<div className="mx-6 my-4">
				<div className="font-bold text-xl text-grey-900 mb-2"> {meatName}</div>
				<p className="text-grey-700 text-base">{shortDescription}</p>
			</div>
			<div className="mx-6 my-4">
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#photography
				</motion.span>
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#travel
				</motion.span>
				<motion.span
					whileHover={{ scale: 1.1 }}
					className="cursor-pointer inline-block hover:bg-teal-200 rounded-full px-3 py-1 text-sm font-semibold text-teal-700 hover:text-teal-900"
				>
					#winter
				</motion.span>
			</div>
		</motion.div>
	);
};

export default SearchResultItem;
